body.script:
$(document).ready(function() {
  $("#memo").wysihtml5({toolbar: {fa:true}});
  $("div#main").on("click", "[data-confirm='yes']", function(event) {
    event.preventDefault();
    var href = $(this).attr("href"), prompt = $(this).data("prompt");
    bootbox.confirm("Are you sure you want to "+prompt+"?", function(result) {
      if (result) {
        $.ajax({type: 'DELETE', url: href, success: function(data, status, xhr) {
          bootbox.alert("Delete status="+status+" data="+JSON.stringify(data));
          // if (data.ok) { location.reload(true) }
        }})
      }
    })
  })
})
